// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.24;

/**
* @dev Interface for SingleOwnerPlugin. Other plugin can import type(ISingleOwnerPlugin).interfaceId as dependency.
*      Single owner plugin which is forked from OZ's Ownable. This plugin allows MSCA to be owned by an EOA or another smart contract (which supports 1271).
*      ERC4337's bundler validation rules (canonical mempool) forbid the opcodes with different outputs between the simulation and execution.
*      Meanwhile, bundler validation rules enforce storage access rules that allows the entity to use sender's associated storage.
*      When staked, an entity is also allowed to use its own associated storage.
*      If the owner is a smart contract, the validation should not use any banned opcodes and violate any storage rules.
*      If the owner uses a storage slot not associated with itself, then the validation would fail.
*/
interface ISingleOwnerPlugin {
    // function id to plugin itself
    enum FunctionId {
        RUNTIME_VALIDATION_OWNER_OR_SELF,
        USER_OP_VALIDATION_OWNER
    }
    event OwnershipTransferred(address indexed account, address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current msg.sender.
     */
    function transferOwnership(address newOwner) external;

    /**
     * @dev Returns the address of the current msg.sender.
     */
    function getOwner() external view returns (address);

    /**
     * @dev Returns the address of the account.
     */
    function getOwnerOf(address account) external view returns (address);
}

src/msca/v1/plugins/v1_0_0/acl/SingleOwn.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.24;

import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {IERC1271} from "@openzeppelin/contracts/interfaces/IERC1271.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {SignatureChecker} from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import {UserOperation} from "@account-abstraction/contracts/interfaces/UserOperation.sol";
import {BasePlugin} from "../../BasePlugin.sol";
import {IStandardExecutor} from "../../../interfaces/IStandardExecutor.sol";
import {IPluginManager} from "../../../interfaces/IPluginManager.sol";
import {ISingleOwnerPlugin} from "./ISingleOwnerPlugin.sol";
import "../../../common/Errors.sol";
import "../../../common/Structs.sol";
import "../../../common/PluginManifest.sol";
import "../../../common/Constants.sol";

/**
* @dev Single owner plugin which is forked from OZ's Ownable. This plugin allows MSCA to be owned by an EOA or another smart contract (which supports 1271).
*      ERC4337's bundler validation rules (canonical mempool) forbid the opcodes with different outputs between the simulation and execution.
*      Meanwhile, bundler validation rules enforce storage access rules that allows the entity to use sender's associated storage.
*      When staked, an entity is also allowed to use its own associated storage.
*      If the owner is a smart contract, the validation should not use any banned opcodes and violate any storage rules.
*      If the owner uses a storage slot not associated with itself, then the validation would fail.
*/
contract SingleOwnerPlugin is BasePlugin, ISingleOwnerPlugin, IERC1271 {
    using ECDSA for bytes32;
    string public constant NAME = "Single Owner Plugin";
    string constant TRANSFER_OWNERSHIP = "Transfer_Ownership";
    // MSCA => owner
    mapping(address => address) internal _mscaOwners;

    error NoOwnerForMSCA(address account);

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current msg.sender.
     */
    function transferOwnership(address newOwner) external {
        _transferOwnership(newOwner);
    }

    /**
     * @dev Returns the address of the current msg.sender.
     */
    function getOwner() external view returns (address) {
        return _mscaOwners[msg.sender];
    }

    /**
     * @dev Returns the address of the account.
     */
    function getOwnerOf(address account) external view returns (address) {
        return _mscaOwners[account];
    }

    /// @inheritdoc IERC1271
    function isValidSignature(bytes32 hash, bytes memory signature) external override view returns (bytes4) {
        if (_verifySignature(hash, signature)) {
            return EIP1271_VALID_SIGNATURE;
        }
        return EIP1271_INVALID_SIGNATURE;
    }

    /// @inheritdoc BasePlugin
    function onInstall(bytes calldata data) external override {
        _transferOwnership(abi.decode(data, (address)));
    }

    /// @inheritdoc BasePlugin
    function onUninstall(bytes calldata data) external override {
        (data);
        _transferOwnership(address(0));
    }

    /// @inheritdoc BasePlugin
    function userOpValidationFunction(uint8 functionId, UserOperation calldata userOp, bytes32 userOpHash)
    external view override returns (uint256 validationData) {
        if (functionId != uint8(FunctionId.USER_OP_VALIDATION_OWNER)) {
            revert InvalidValidationFunctionId(functionId);
        }
        if (_verifySignature(userOpHash, userOp.signature)) {
            return SIG_VALIDATION_SUCCEEDED;
        }
        return SIG_VALIDATION_FAILED;
    }

    /// @inheritdoc BasePlugin
    function runtimeValidationFunction(uint8 functionId, address sender, uint256 value, bytes calldata data)
    external view override {
        (value, data);
        if (functionId != uint8(FunctionId.RUNTIME_VALIDATION_OWNER_OR_SELF)) {
            revert InvalidValidationFunctionId(functionId);
        }
        // the sender should be the owner of the account or itself
        // msg.sender is MSCA
        if (sender == _mscaOwners[msg.sender] || sender == msg.sender) {
            return;
        }
        revert UnauthorizedCaller();
    }

    /// @inheritdoc BasePlugin
    function pluginManifest() external pure override returns (PluginManifest memory) {
        PluginManifest memory manifest;
        manifest.executionFunctions = new bytes4[](4);
        manifest.executionFunctions[0] = this.transferOwnership.selector;
        manifest.executionFunctions[1] = this.getOwner.selector;
        manifest.executionFunctions[2] = this.getOwnerOf.selector;
        manifest.executionFunctions[3] = this.isValidSignature.selector;

        ManifestFunction memory userOpValidationAssociatedFunction = ManifestFunction(ManifestAssociatedFunctionType.SELF, uint8(FunctionId.USER_OP_VALIDATION_OWNER), 0);
        // the following function calls (from entry point) should be gated by the userOpValidationAssociatedFunction
        manifest.userOpValidationFunctions = new ManifestAssociatedFunction[](7);
        // plugin functions
        manifest.userOpValidationFunctions[0] = ManifestAssociatedFunction(this.transferOwnership.selector, userOpValidationAssociatedFunction);
        // native functions
        manifest.userOpValidationFunctions[1] = ManifestAssociatedFunction(IStandardExecutor.execute.selector, userOpValidationAssociatedFunction);
        manifest.userOpValidationFunctions[2] = ManifestAssociatedFunction(IStandardExecutor.executeBatch.selector, userOpValidationAssociatedFunction);
        manifest.userOpValidationFunctions[3] = ManifestAssociatedFunction(IPluginManager.installPlugin.selector, userOpValidationAssociatedFunction);
        manifest.userOpValidationFunctions[4] = ManifestAssociatedFunction(IPluginManager.uninstallPlugin.selector, userOpValidationAssociatedFunction);
        manifest.userOpValidationFunctions[5] = ManifestAssociatedFunction(UUPSUpgradeable.upgradeTo.selector, userOpValidationAssociatedFunction);
        manifest.userOpValidationFunctions[6] = ManifestAssociatedFunction(UUPSUpgradeable.upgradeToAndCall.selector, userOpValidationAssociatedFunction);

        ManifestFunction memory runtimeValidationAssociatedFunction = ManifestFunction(ManifestAssociatedFunctionType.SELF, uint8(FunctionId.RUNTIME_VALIDATION_OWNER_OR_SELF), 0);
        ManifestFunction memory runtimeAlwaysAllowAssociatedFunction = ManifestFunction(ManifestAssociatedFunctionType.RUNTIME_VALIDATION_ALWAYS_ALLOW, 0, 0);
        // the following direct function calls (from EOA/SC) should be gated by the runtimeValidationAssociatedFunction
        manifest.runtimeValidationFunctions = new ManifestAssociatedFunction[](10);
        // plugin functions
        manifest.runtimeValidationFunctions[0] = ManifestAssociatedFunction(this.transferOwnership.selector, runtimeValidationAssociatedFunction);
        // native functions
        manifest.runtimeValidationFunctions[1] = ManifestAssociatedFunction(IStandardExecutor.execute.selector, runtimeValidationAssociatedFunction);
        manifest.runtimeValidationFunctions[2] = ManifestAssociatedFunction(IStandardExecutor.executeBatch.selector, runtimeValidationAssociatedFunction);
        manifest.runtimeValidationFunctions[3] = ManifestAssociatedFunction(IPluginManager.installPlugin.selector, runtimeValidationAssociatedFunction);
        manifest.runtimeValidationFunctions[4] = ManifestAssociatedFunction(IPluginManager.uninstallPlugin.selector, runtimeValidationAssociatedFunction);
        manifest.runtimeValidationFunctions[5] = ManifestAssociatedFunction(UUPSUpgradeable.upgradeTo.selector, runtimeValidationAssociatedFunction);
        manifest.runtimeValidationFunctions[6] = ManifestAssociatedFunction(UUPSUpgradeable.upgradeToAndCall.selector, runtimeValidationAssociatedFunction);
        // always allow the following direct function calls (from EOA/SC)
        manifest.runtimeValidationFunctions[7] = ManifestAssociatedFunction(this.getOwner.selector, runtimeAlwaysAllowAssociatedFunction);
        manifest.runtimeValidationFunctions[8] = ManifestAssociatedFunction(this.getOwnerOf.selector, runtimeAlwaysAllowAssociatedFunction);
        manifest.runtimeValidationFunctions[9] = ManifestAssociatedFunction(this.isValidSignature.selector, runtimeAlwaysAllowAssociatedFunction);
        manifest.interfaceIds = new bytes4[](2);
        manifest.interfaceIds[0] = type(IERC1271).interfaceId;
        manifest.interfaceIds[1] = type(ISingleOwnerPlugin).interfaceId;
        return manifest;
    }

    /// @inheritdoc BasePlugin
    function pluginMetadata() external pure virtual override returns (PluginMetadata memory) {
        PluginMetadata memory metadata;
        metadata.name = NAME;
        metadata.version = PLUGIN_VERSION_1;
        metadata.author = PLUGIN_AUTHOR;

        // Permission descriptions
        metadata.permissionDescriptors = new SelectorPermission[](1);
        metadata.permissionDescriptors[0] = SelectorPermission({
            functionSelector: this.transferOwnership.selector,
            permissionDescription: TRANSFER_OWNERSHIP
        });
        return metadata;
    }

    /// @inheritdoc BasePlugin
    function supportsInterface(bytes4 interfaceId) public view override returns (bool) {
        return interfaceId == type(ISingleOwnerPlugin).interfaceId || super.supportsInterface(interfaceId);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal {
        address oldOwner = _mscaOwners[msg.sender];
        _mscaOwners[msg.sender] = newOwner;
        emit OwnershipTransferred(msg.sender, oldOwner, newOwner);
    }

    /**
     * @dev For EOA owner, run ecrecover. For smart contract owner, run 1271 staticcall.
     */
    function _verifySignature(bytes32 hash, bytes memory signature) internal view returns (bool) {
        address owner = _mscaOwners[msg.sender];
        if (owner == address(0)) {
            revert NoOwnerForMSCA(msg.sender);
        }
        // EOA owner (ECDSA)
        // if the signature (personal sign) is signed over userOpHash.toEthSignedMessageHash (prepended with 'x\x19Ethereum Signed Message:\n32'), then we recover using userOpHash.toEthSignedMessageHash;
        // or if the signature (typed data sign) is signed over userOpHash directly, then we recover userOpHash directly
        (address recovered, ECDSA.RecoverError error) = hash.toEthSignedMessageHash().tryRecover(signature);
        if (error == ECDSA.RecoverError.NoError && recovered == owner) {
            return true;
        }
        (recovered, error) = hash.tryRecover(signature);
        if (error == ECDSA.RecoverError.NoError && recovered == owner) {
            return true;
        }
        if (SignatureChecker.isValidERC1271SignatureNow(owner, hash, signature)) {
            // smart contract owner.isValidSignature should be smart enough to sign over the non-modified hash or over
            // the hash that is modified in the way owner would expect
            return true;
        }
        return false;
    }
}

src/msca/v1/plugins/v1_0_0/addressbook/I.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.24;

/**
* @dev Interface for AddressBookPlugin. Other plugin can import type(IAddressBookPlugin).interfaceId as dependency.
*      This plugin allows MSCA to check if the destination address is allowed to receive native or a specific token.
*      The implementation could store an internal allowedRecipients similar to mapping(MSCA => associatedStorageLinkedList(recipient)).
*      By default the recipient is allowed to accept any tokens if it's added to the address book.
*      For implementations, please be mindful that bundler validation rules only allow the entity to access the storage
*      that's associated with the sender.
*/
interface IAddressBookPlugin {

    // function id to plugin itself
    enum FunctionId {
        PRE_USER_OP_VALIDATION_HOOK_ADDRESS_BOOK
    }

    // Standard executor
    struct CallWithRecipient {
        // The target address for the account to call.
        address target;
        // The value to send with the call.
        uint256 value;
        // The calldata for the call.
        bytes data;
        // The beneficiary of the call.
        address recipient;
    }

    event AllowedAddressAdded(address indexed account, address indexed recipient);
    event AllowedAddressRemoved(address indexed account, address indexed recipient);

    /**
     * @dev Execute the transaction with an additional address book check.
     */
    function executeWithAddressBookProtection(CallWithRecipient calldata call) external returns (bytes memory);

    /**
     * @dev Batch execute the transactions with additional address book checks.
     */
    function executeBatchWithAddressBookProtection(CallWithRecipient[] calldata calls) external returns (bytes[] memory);

    /**
     * @dev Add allowed recipient. By default the recipient is allowed to accept all tokens.
     * Can only be called by the current msg.sender.
     */
    function addAllowedAddress(address recipient) external;

    /**
     * @dev Remove allowed recipient.
     * Can only be called by the current msg.sender.
     */
    function removeAllowedAddress(address recipient) external;

    /**
     * @dev Returns the allowed addresses of the current MSCA.
     */
    function getAddressBook(address account) external view returns (address[] memory);
}

src/msca/v1/plugins/v1_0_0/utility/Defau.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.24;

import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import {IERC777Recipient} from "@openzeppelin/contracts/interfaces/IERC777Recipient.sol";
import {IERC1155Receiver} from "@openzeppelin/contracts/interfaces/IERC1155Receiver.sol";
import {BasePlugin} from "../../BasePlugin.sol";
import "../../../common/Structs.sol";
import "../../../common/PluginManifest.sol";
import "../../../common/Constants.sol";

/**
* @dev Default token callback handler plugin. Similar to DefaultCallbackHandler.
*      This plugin allows MSCA to receive tokens such as 721, 1155 and 777.
*/
contract DefaultTokenCallbackPlugin is BasePlugin, IERC721Receiver, IERC1155Receiver, IERC777Recipient {
    string public constant NAME = "Default Token Callback Plugin";

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC1155Receiver.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC1155Receiver.onERC1155BatchReceived.selector;
    }

    // ERC777
    function tokensReceived(
        address operator,
        address from,
        address to,
        uint256 amount,
        bytes calldata userData,
        bytes calldata operatorData
    ) external pure override {}

    /// @inheritdoc BasePlugin
    function pluginManifest() external pure override returns (PluginManifest memory) {
        PluginManifest memory manifest;
        manifest.executionFunctions = new bytes4[](4);
        manifest.executionFunctions[0] = this.onERC721Received.selector;
        manifest.executionFunctions[1] = this.onERC1155Received.selector;
        manifest.executionFunctions[2] = this.onERC1155BatchReceived.selector;
        manifest.executionFunctions[3] = this.tokensReceived.selector;
        // only runtimeValidationFunctions is needed since token contracts callback to the plugin
        manifest.runtimeValidationFunctions = new ManifestAssociatedFunction[](4);
        // we can consider implementing more complex logic to reject the scamming tokens in the future
        ManifestFunction memory runtimeValidationAlwaysAllow = ManifestFunction(ManifestAssociatedFunctionType.RUNTIME_VALIDATION_ALWAYS_ALLOW, 0, 0);
        manifest.runtimeValidationFunctions[0] = ManifestAssociatedFunction(this.onERC721Received.selector, runtimeValidationAlwaysAllow);
        manifest.runtimeValidationFunctions[1] = ManifestAssociatedFunction(this.onERC1155Received.selector, runtimeValidationAlwaysAllow);
        manifest.runtimeValidationFunctions[2] = ManifestAssociatedFunction(this.onERC1155BatchReceived.selector, runtimeValidationAlwaysAllow);
        manifest.runtimeValidationFunctions[3] = ManifestAssociatedFunction(this.tokensReceived.selector, runtimeValidationAlwaysAllow);
        manifest.interfaceIds = new bytes4[](3);
        manifest.interfaceIds[0] = type(IERC721Receiver).interfaceId;
        manifest.interfaceIds[1] = type(IERC1155Receiver).interfaceId;
        manifest.interfaceIds[2] = type(IERC777Recipient).interfaceId;
        return manifest;
    }

    /// @inheritdoc BasePlugin
    function pluginMetadata() external pure virtual override returns (PluginMetadata memory) {
        PluginMetadata memory metadata;
        metadata.name = NAME;
        metadata.version = PLUGIN_VERSION_1;
        metadata.author = PLUGIN_AUTHOR;
        return metadata;
    }
}

