        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
}

src/msca/account/BaseMSCA.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "@account-abstraction/contracts/interfaces/IEntryPoint.sol";
import "@account-abstraction/contracts/interfaces/IAccount.sol";
import "@account-abstraction/contracts/interfaces/UserOperation.sol";
import "../common/Errors.sol";
import "./BaseMSCAStorage.sol";
import "../../utils/MSCAUtils.sol";
import "../../utils/ExecutionUtils.sol";
import "../interfaces/IPlugin.sol";
import "../../utils/MSCAUtils.sol";
import "../common/Structs.sol";
import "../common/Constants.sol";
import "../managers/PluginManager.sol";
import "../managers/PluginLoupe.sol";
import "../managers/StandardExecutor.sol";
import "../managers/PluginExecutor.sol";

/**
 * @dev Base MSCA implementation with **authentication**.
 * This contract provides the basic logic for implementing the MSCA interfaces;
 * specific account implementation should inherit this contract.
 */
abstract contract BaseMSCA is IAccount, BaseMSCAStorage, PluginManager, PluginLoupe, StandardExecutor, PluginExecutor, Errors {
    using UserOperationLib for UserOperation;
    using MSCAUtils for uint256;
    using MSCAUtils for ValidationData;

    /**
     * @dev Ensure the request comes from the known entrypoint.
     */
    modifier onlyFromEntryPoint() {
        if (msg.sender != address(entryPoint())) {
            revert UnauthorizedCaller();
        }
        _;
    }

    /**
     * @dev This function allows entry point or SA itself to execute certain actions.
     * If the caller is not authorized, the function will revert with an error message.
     * @notice This function acts as modifier and is marked as internal to be be called
     * within the contract itself only.
     */
    modifier onlyFromEntryPointOrSelf() {
        if (msg.sender != address(entryPoint()) && msg.sender != address(this)) {
            revert UnauthorizedCaller();
        }
        _;
    }

    /**
     * @dev Validate user's signature and nonce.
     * subclass doesn't need to override this method. Instead, it should override the specific internal validation methods.
     */
    function validateUserOp(UserOperation calldata userOp, bytes32 userOpHash, uint256 missingAccountFunds)
    external override virtual
    onlyFromEntryPoint
    returns (uint256 validationData) {
        validationData = _authenticateAndAuthorizeUserOp(userOp, userOpHash);
        _validateNonce(userOp.nonce);
        _payPrefund(missingAccountFunds);
    }

    /**
     * @dev Return the account nonce.
     * This method returns the next sequential nonce.
     * For a nonce of a specific key, use `entrypoint.getNonce(account, key)`
     */
    function getNonce() public view virtual returns (uint256) {
        return entryPoint().getNonce(address(this), 0);
    }

    /**
     * @dev Return the entryPoint used by this account.
     * subclass should return the current entryPoint used by this account.
     */
    function entryPoint() public view virtual returns (IEntryPoint);

    /**
     * @dev Authenticate and authorize this userOp.
     * @param userOp validate the userOp.signature field
     * @param userOpHash convenient field: the hash of the request, to check the signature against
     *          (also hashes the entrypoint and chain id)
     * @return validationData signature and time-range of this operation
     *      <20-byte> sigAuthorizer - 0 for valid signature, 1 to mark signature failure,
     *         otherwise, an address of an "authorizer" contract.
     *      <6-byte> validUntil - last timestamp this operation is valid. 0 for "indefinite"
     *      <6-byte> validAfter - first timestamp this operation is valid
     *      If the account doesn't use time-range, it is enough to return SIG_VALIDATION_FAILED value (1) for signature failure.
     *      Note that the validation code cannot use block.timestamp (or block.number) directly.
     */
    function _authenticateAndAuthorizeUserOp(UserOperation calldata userOp, bytes32 userOpHash)
    internal virtual returns (uint256 validationData) {
        validationData = SIG_VALIDATION_FAILED;
        if (userOp.callData.length < 4) {
            revert NotFoundExecutor();
        }
        // Given the execution function selector, find the userOp validator
        bytes4 executionSelector = bytes4(userOp.callData[0 : 4]);
        if (executionSelector == bytes4(0)) {
            revert NotFoundExecutor();
        }
        ExecutionFunctionConfig memory executionFunctionConfig = executorToValidator[executionSelector];
        if (executionFunctionConfig.plugin == address(0)) {
            revert NotFoundValidator();
        }
        ValidationData memory parsedValidationData = validationData._parseValidationData();
        AssociatedFunction[] memory preUserOpValidatorHooks = executorToValidatorToHooks[executionSelector][executionFunctionConfig.userOpValidator.plugin][executionFunctionConfig.userOpValidator.functionId];
        for (uint256 i = 0; i < preUserOpValidatorHooks.length; i++) {
            AssociatedFunction memory preUserOpValidatorHook = preUserOpValidatorHooks[i];
            IPlugin preUserOpValidatorHookPlugin = IPlugin(preUserOpValidatorHook.plugin);
            // TODO: check ManifestFunctionType functionType
            uint256 newValidationData = preUserOpValidatorHookPlugin.preUserOpValidationHook(preUserOpValidatorHook.functionId, userOp, userOpHash);
            ValidationData memory parsedNewValidationData = newValidationData._parseValidationData();
            if (parsedNewValidationData.authorizer != address(0) && parsedNewValidationData.authorizer != address(1)) {
                revert InvalidAuthorizer();
            }
            if (parsedNewValidationData.authorizer == address(1)) {
                return newValidationData;
            }
            parsedValidationData = MSCAUtils._intersectValidationData(parsedValidationData, parsedNewValidationData);
        }
        // TODO: If the call is to a standard execution function, then the modular account MUST verify that the provided validator in calldata has previously been associated with either of the standard execution functions. If it was previously added, the specified user operation validator MUST be run.
        // TODO: call validator
        IPlugin validationDataFromValidator = IPlugin(executionFunctionConfig.userOpValidator.plugin);
        validationData = MSCAUtils._intersectValidationData(validationDataFromValidator.userOpValidator(executionFunctionConfig.userOpValidator.functionId, userOp, userOpHash)._parseValidationData(),
            parsedValidationData)._packValidationData();
    }

    /**
     * @dev Sends to the entrypoint (msg.sender) the missing funds for this transaction.
     * subclass MAY override this method for better funds management
     * (e.g. send to the entryPoint more than the minimum required, so that in future transactions
     * it will not be required to send again)
     * @param missingAccountFunds the minimum value this method should send the entrypoint.
     *  this value MAY be zero, in case there is enough deposit, or the userOp has a paymaster.
     */
    function _payPrefund(uint256 missingAccountFunds) internal virtual {
        if (missingAccountFunds != 0) {
            (bool success,) = payable(msg.sender).call{value : missingAccountFunds, gas : type(uint256).max}("");
            (success);
            // ignore failure (its EntryPoint's job to verify, not account.)
        }
    }

    /**
     * @dev Validate the nonce of the UserOperation.
     * This method may validate the nonce requirement of this account.
     * e.g.
     * To limit the nonce to use sequenced UserOps only (no "out of order" UserOps):
     *      `require(nonce < type(uint64).max)`
     * For a hypothetical account that *requires* the nonce to be out-of-order:
     *      `require(nonce & type(uint64).max == 0)`
     *
     * The actual nonce uniqueness is managed by the EntryPoint, and thus no other
     * action is needed by the account itself.
     *
     * @param nonce to validate
     *
     * solhint-disable-next-line no-empty-blocks
     */
    function _validateNonce(uint256 nonce) internal view virtual {

    }
}

