// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "@account-abstraction/contracts/interfaces/IEntryPoint.sol";
import "../common/Structs.sol";

contract BaseMSCAStorage {
    // 4337 related immutable storage
    IEntryPoint public immutable entryPointAddress;

    constructor(IEntryPoint _newEntryPoint) {
        entryPointAddress = _newEntryPoint;
    }

    // the mapping between execution function selector to validator (1:1 relation)
    mapping(bytes4 => ExecutionFunctionConfig) executorToValidator;

    // the mapping between executor to validator to validation hooks (1:n relation)
    mapping(bytes4 => mapping(address => mapping(uint8 => AssociatedFunction[]))) executorToValidatorToHooks;

    // the mapping between execution function selector to pre & post exec hooks (1:n relation)
    mapping(bytes4 => ExecutionHook[]) executorToHooks;

}

