// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import {IPaymaster} from "@account-abstraction/contracts/interfaces/IPaymaster.sol";
import {IEntryPoint} from "@account-abstraction/contracts/interfaces/IEntryPoint.sol";
import {PaymasterUtils} from "../utils/PaymasterUtils.sol";
import {UserOperation} from "@account-abstraction/contracts/interfaces/UserOperation.sol";
import {IStakeManager} from "@account-abstraction/contracts/interfaces/IStakeManager.sol";

/**
* The paymaster must also have a deposit, which the entry point will charge UserOperation costs from.
* The deposit (for paying gas fees) is separate from the stake (which is locked).
* Note that this signature is NOT a replacement for the account-specific signature:
* - the paymaster checks a signature to agree to pay for gas.
* - the account checks a signature prove identity and account ownership.
* Since this contract is upgradable, we do not allow use either selfdestruct or delegatecall to prevent a malicious actor from
* destroying the logic contract.
*/
abstract contract BasePaymaster is IPaymaster, Initializable, UUPSUpgradeable, OwnableUpgradeable, PausableUpgradeable {
    // global entry point
    IEntryPoint public immutable entryPoint;
    /**
    * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[50] private __gap;

    /// @inheritdoc UUPSUpgradeable
    // The {_authorizeUpgrade} function must be overridden to include access restriction to the upgrade mechanism.
    // Authorize the owner to upgrade the contract.
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    /// @custom:oz-upgrades-unsafe-allow constructor
    // for immutable values in implementations
    constructor(IEntryPoint _newEntryPoint) {
        entryPoint = _newEntryPoint;
        // lock the implementation contract so it can only be called from proxies
        _disableInitializers();
    }

    function __BasePaymaster_init(address _newOwner) internal onlyInitializing {
        __UUPSUpgradeable_init();
        __Ownable_init();
        transferOwnership(_newOwner);
        __Pausable_init();
    }

    /// @inheritdoc IPaymaster
    function validatePaymasterUserOp(UserOperation calldata userOp, bytes32 userOpHash, uint256 maxCost)
    external whenNotPaused override returns (bytes memory context, uint256 validationData) {
        _requireFromEntryPoint();
        return _validatePaymasterUserOp(userOp, userOpHash, maxCost);
    }

    function _validatePaymasterUserOp(UserOperation calldata userOp, bytes32 userOpHash, uint256 maxCost)
    internal virtual returns (bytes memory context, uint256 validationData);

    /// @inheritdoc IPaymaster
    function postOp(PostOpMode mode, bytes calldata context, uint256 actualGasCost) external whenNotPaused override {
        _requireFromEntryPoint();
        _postOp(mode, context, actualGasCost);
    }

    function _postOp(PostOpMode mode, bytes calldata context, uint256 actualGasCost) internal virtual {}

    /**
     * add a deposit for this paymaster, used for paying for transaction fees
     */
    function deposit() public whenNotPaused payable {
        entryPoint.depositTo{value : msg.value}(address(this));
    }

    /**
     * return current paymaster's deposit on the entryPoint.
     */
    function getDeposit() public view returns (uint256) {
        return entryPoint.balanceOf(address(this));
    }

    /**
    * return current paymaster's full deposit&stake information on the entryPoint.
    */
    function getDepositInfo() public view returns (IStakeManager.DepositInfo memory info) {
        return entryPoint.getDepositInfo(address(this));
    }

    /**
    * add stake for this paymaster.
    * This method can also carry eth value to add to the current stake.
    * @param unstakeDelaySec - the unstake delay for this paymaster. Can only be increased.
    */
    function addStake(uint32 unstakeDelaySec) public onlyOwner whenNotPaused payable {
        entryPoint.addStake{value : msg.value}(unstakeDelaySec);
    }

    /**
     * unlock the stake, in order to withdraw it.
     * The paymaster can't serve requests once unlocked, until it calls addStake again
     */
    function unlockStake() public onlyOwner whenNotPaused {
        entryPoint.unlockStake();
    }

    /**
     * withdraw the entire paymaster's stake.
     * stake must be unlocked first (and then wait for the unstakeDelay to be over)
     * @param withdrawAddress the address to send withdrawn value.
     */
    function withdrawStake(address payable withdrawAddress) public onlyOwner whenNotPaused {
        entryPoint.withdrawStake(withdrawAddress);
    }

    /// validate the call is made from a valid entrypoint
    function _requireFromEntryPoint() internal view {
        require(msg.sender == address(entryPoint), "Sender not EntryPoint");
    }

    function pause() public onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() public onlyOwner whenPaused {
        _unpause();
    }

    function withdrawTo(address payable withdrawAddress, uint256 amount) public onlyOwner whenNotPaused {
        entryPoint.withdrawTo(withdrawAddress, amount);
    }
}

src/paymaster/v1/permissioned/SponsorPay.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

/* solhint-disable reason-string */

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "@account-abstraction/contracts/interfaces/IPaymaster.sol";
import "@account-abstraction/contracts/interfaces/IEntryPoint.sol";
import "../../../utils/PaymasterUtils.sol";
import {_packValidationData} from "@account-abstraction/contracts/core/Helpers.sol";
import "../../BasePaymaster.sol";

/**
* For sponsor mode, a signature is required from Circle. The purpose of signature is mostly used to allow Circle
* to offer the gasless experience (free for end user) or other services.
* In this mode, the paymaster uses external service to decide whether to pay for the UserOp.
* The calling user must pass the UserOp to that external signer first, which performs whatever
* off-chain verification before signing the UserOp.
* The off-chain service could enable the user to pay for the gas cost with a credit card, subscription, or free, etc.
* The paymaster verifies the external signer has signed the request in method _validatePaymasterUserOp().
* getHash() returns a hash we're going to sign off-chain and validate on-chain.
* Note that this signature is NOT a replacement for the account-specific signature:
* - the paymaster checks a signature to agree to pay for gas.
* - the account checks a signature prove identity and account ownership.
* Since this contract is upgrable, we do not allow use either selfdestruct or delegatecall to prevent a malicious actor from
* attacking the logic contract.
*/
contract SponsorPaymaster is BasePaymaster {

    using UserOperationLib for UserOperation;
    using PaymasterUtils for UserOperation;

    // trusted offline signer
    address public verifyingSigner;

    // constants still work for upgradable contracts because the compiler does not reserve storage slot
    // and every occurrence is replaced by the respective constant expression
    uint256 private constant TIMESTAMP_START = 20;
    uint256 private constant SIGNATURE_START = 84;

    /// @custom:oz-upgrades-unsafe-allow constructor
    // for immutable values in implementations
    constructor(IEntryPoint _newEntryPoint) BasePaymaster(_newEntryPoint) {
        // lock the implementation contract so it can only be called from proxies
        _disableInitializers();
    }

    function initialize(address _newOwner, address _newVerifyingSigner) public initializer {
        __BasePaymaster_init(_newOwner);
        verifyingSigner = _newVerifyingSigner;
    }

    /**
     * Verify our external signer has signed this request.
     * The "paymasterAndData" is expected to be the paymaster and a signature over the entire request params.
     * paymasterAndData[:20] : address(this)
     * paymasterAndData[20:84] : abi.encode(validUntil, validAfter)
     * paymasterAndData[84:] : signature
     */
    function _validatePaymasterUserOp(UserOperation calldata userOp, bytes32 userOpHash, uint256 maxCost)
    internal override view returns (bytes memory context, uint256 validationData) {
        // unused
        (userOpHash, maxCost);

        (uint48 validUntil, uint48 validAfter, bytes memory signature) = parsePaymasterAndData(userOp.paymasterAndData);

        // calculate hash and check sig if applicable
        bytes32 hash = ECDSA.toEthSignedMessageHash(getHash(userOp, validUntil, validAfter));
        // check signature, we don't need the offline service to be a smart contract;
        // it should work as long as it signs the data and we can verify it;
        // isValidSignatureNow would do ECDSA.tryRecover first;
        // don't revert on signature failure: return SIG_VALIDATION_FAILED
        if (!SignatureChecker.isValidSignatureNow(verifyingSigner, hash, signature)) {
            return ("", _packValidationData(true, validUntil, validAfter));
        }

        // no need for other on-chain validation: entire UserOp should have been checked
        // by the external service prior to signing it.
        // no context returned because of no postOp activity
        return ("", _packValidationData(false, validUntil, validAfter));
    }

    /**
    * paymasterAndData[:20] : address(this)
    * paymasterAndData[20:84] : abi.encode(validUntil, validAfter)
    * paymasterAndData[84:] : signature
    */
    function parsePaymasterAndData(bytes calldata paymasterAndData)
    public pure returns (uint48 validUntil, uint48 validAfter, bytes calldata signature) {
        (validUntil, validAfter) = abi.decode(paymasterAndData[TIMESTAMP_START : SIGNATURE_START], (uint48, uint48));
        signature = paymasterAndData[SIGNATURE_START :];
    }

    /**
     * return the hash we're going to sign off-chain (and validate on-chain)
     * this method is called by the off-chain service, to sign the request.
     * it is called on-chain from the validatePaymasterUserOp, to validate the signature.
     * note that this signature covers all fields of the UserOperation, except the "paymasterAndData",
     * which will carry the signature itself.
     * struct UserOperation {
     *   address sender;
     *   uint256 nonce;
     *   bytes initCode;
     *   bytes callData;
     *   uint256 callGasLimit;
     *   uint256 verificationGasLimit;
     *   uint256 preVerificationGas;
     *   uint256 maxFeePerGas;
     *   uint256 maxPriorityFeePerGas;
     *   bytes paymasterAndData;
     *   bytes signature;
     * }
     */
    function getHash(UserOperation calldata userOp, uint48 validUntil, uint48 validAfter)
    public view returns (bytes32) {
        // can't use userOp.hash(), since it contains also the paymasterAndData itself.
        // EP manages nonce via NonceManager so senderNonce is redundant
        return keccak256(abi.encode(
                userOp.packUpToPaymasterAndData(),
                block.chainid,
                address(this),
                userOp.nonce,
                validUntil,
                validAfter
            ));
    }

    // key rotation
    function setVerifyingSigner(address _newVerifyingSigner) external onlyOwner whenNotPaused {
        verifyingSigner = _newVerifyingSigner;
    }
}

src/utils/ExecutionUtils.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

// solhint-disable no-inline-assembly

/**
 * Utility functions helpful when making different kinds of contract calls in Solidity.
 * For inline assembly, please refer to https://docs.soliditylang.org/en/latest/assembly.html
 * For opcodes, please refer to https://ethereum.org/en/developers/docs/evm/opcodes/ and https://www.evm.codes/
 */
library ExecutionUtils {

    function call(address to,
        uint256 value,
        bytes memory data
    ) internal returns (bool success, bytes memory returnData) {
        assembly {
            success := call(gas(), to, value, add(data, 0x20), mload(data), 0, 0)
            let len := returndatasize()
            let ptr := mload(0x40)
            mstore(0x40, add(ptr, add(len, 0x20)))
            mstore(ptr, len)
            returndatacopy(add(ptr, 0x20), 0, len)
            returnData := ptr
        }
    }

    function revertWithData(bytes memory returnData) internal pure {
        assembly {
            revert(add(returnData, 32), mload(returnData))
        }
    }

    function callAndRevert(address to,
        uint256 value,
        bytes memory data) internal {
        (bool success, bytes memory returnData) = call(to, value, data);
        if (!success) {
            revertWithData(returnData);
        }
    }
}

src/utils/MSCAUtils.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;
import "../msca/common/Structs.sol";

/**
 * @dev ValidateUserOp returns a uint256, with is created by `_packedValidationData` and parsed by `_parseValidationData`
 * validAfter - this UserOp is valid only after this timestamp.
 * validaUntil - this UserOp is valid only up to this timestamp.
 * authorizer - address(0) - the account validated the signature by itself.
 *              address(1) - the account failed to validate the signature.
 *              otherwise - this is an address of a signature aggregator that must be used to validate the signature.
 */
library MSCAUtils {

    /**
     * @dev Pack into the format as validAfter | validUntil | authorizer.
     */
    function _packValidationData(ValidationData memory data) internal pure returns (uint256) {
        return uint160(data.authorizer) | (uint256(data.validUntil) << 160) | (uint256(data.validAfter) << (160 + 48));
    }

    /**
     * @dev Unpack the packed format from validAfter | validUntil | authorizer.
     */
    function _parseValidationData(uint256 validationDataInt) internal pure returns (ValidationData memory validationData) {
        address authorizer = address(uint160(validationDataInt));
        uint48 validUntil = uint48(validationDataInt >> 160);
        if (validUntil == 0) {
            validUntil = type(uint48).max;
        }
        uint48 validAfter = uint48(validationDataInt >> (48 + 160));
        return ValidationData(validAfter, validUntil, authorizer);
    }

    /**
   * @dev Intercept the time bounds.
     */
    function _intersectValidationData(ValidationData memory a, ValidationData memory b) internal pure returns (ValidationData memory validationData) {
        validationData.authorizer = a.authorizer;
        if (validationData.authorizer == address(0)) {
            validationData.authorizer = b.authorizer;
        }
        uint48 validAfterA = a.validAfter;
        uint48 validUntilA = a.validUntil;
        uint48 validAfterB = b.validAfter;
        uint48 validUntilB = b.validUntil;
        if (validAfterA < validAfterB) {
            validationData.validAfter = validAfterB;
        }
        if (validUntilA > validUntilB) {
            validationData.validUntil = validUntilB;
        }
        return validationData;
    }
}

src/utils/PaymasterUtils.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

/* solhint-disable reason-string */
/* solhint-disable no-inline-assembly */

import {UserOperation, UserOperationLib} from "@account-abstraction/contracts/interfaces/UserOperation.sol";

enum ChargeMode {
    GAS_ONLY, // paymaster will charge a network fee
    FREE, // paymaster will sponsor the transaction
    FEE_ONLY, // paymaster will charge a flat fee for its service
    FEE_AND_GAS // paymaster will charge both flat fee and network fee
}

/**
* @dev In chainlink feed aggregator, ETH () pairs (quote is ) have 18 decimal of precisions, e.g. USDC/ETH;
* Non-ETH (quote is not ) pairs have 8 decimals of precisions, e.g. ETH/USDC.
* For ETH/USDC,
* 1. let ethToUSDCFxRate = latestRoundData.answer / 10**feedDecimals;
* 2. let usdcCost = maxETHToBuy * ethToUSDCFxRate;
* 3. let usdcCostInSubunits = usdcCost * 10**usdcDecimals
* Now let's use the above formula to calculate the USDC token cost.
* Let's assume ETH/USDC's latestRoundData.answer (https://etherscan.io/address/0x5f4eC3Df9cbd43714FE2740f5E3616155c5b8419#readContract) is 211331000000 (note the link is actually for ETH/USD),
* let's also get maxETHToBuy (requiredPrefund) from https://aascan.org/, or you can calculate from totalGasLimit * maxFeePerGas,
* let's assume maxETHToBuy(requiredPrefund) is 0.015 (15000000000000000), then apply step 1 ~ 3,
* 1. ethToUSDCFxRate = 211331000000 / 10**8 = 2113.31;
* 2. usdcCost = 0.015 * 2113.31 = 31.69965
* 3. usdcCostInSubunits = 31.69965 * (10**6) = 31699650
*
* fxRate will be provided from an offchain price oracle that's aggregated from different exchanges.
* For more details, please refer to https://docs.chain.link/data-feeds/price-feeds/addresses/?network=ethereum
*/
library PaymasterUtils {
    using UserOperationLib for UserOperation;

    /**
     * struct UserOperation {
     *   address sender;
     *   uint256 nonce;
     *   bytes initCode;
     *   bytes callData;
     *   uint256 callGasLimit;
     *   uint256 verificationGasLimit;
     *   uint256 preVerificationGas;
     *   uint256 maxFeePerGas;
     *   uint256 maxPriorityFeePerGas;
     *   bytes paymasterAndData;
     *   bytes signature;
     * }
     */
    function packUpToPaymasterAndData(UserOperation calldata userOp)
    internal pure returns (bytes memory ret) {
        address sender = userOp.getSender();
        uint256 nonce = userOp.nonce;
        bytes32 hashInitCode = calldataKeccak(userOp.initCode);
        bytes32 hashCallData = calldataKeccak(userOp.callData);
        uint256 callGasLimit = userOp.callGasLimit;
        uint256 verificationGasLimit = userOp.verificationGasLimit;
        uint256 preVerificationGas = userOp.preVerificationGas;
        uint256 maxFeePerGas = userOp.maxFeePerGas;
        uint256 maxPriorityFeePerGas = userOp.maxPriorityFeePerGas;
        return abi.encode(
            sender, nonce,
            hashInitCode, hashCallData,
            callGasLimit, verificationGasLimit, preVerificationGas,
            maxFeePerGas, maxPriorityFeePerGas
        );
    }

    /**
     * Keccak function over calldata.
     * @dev copy calldata into memory, do keccak and drop allocated memory. This is more efficient than letting solidity do it.
     */
    function calldataKeccak(bytes calldata data)
    internal pure returns (bytes32 ret) {
        assembly {
            let mem := mload(0x40)
            let len := data.length
            calldatacopy(mem, data.offset, len)
            ret := keccak256(mem, len)
        }
    }
}
